import React from 'react';
import Roof from 'roof';

const Summary = Roof.createContainer({
  cursors : {
    summary: 'summary'
  },
  render(){
    var families = this.props.summary.get('families')

    var familiesNodes = Object.keys( families  ).map( name=>{
      return <li className='family'>{name}:{families[name]}</li>
    })
    return <ul>
      {familiesNodes}
    </ul>
  }
})


export default Summary;
