var React = require('react')
var Roof = require('roof')


module.exports = Roof.createContainer({
  cursors: {
    users : 'users'
  },
  insertUser(){
    this.props.users.insert({name:'Newer',family:'NotLikeOther'})
  },
  render(){
    //console.log( 'this.props.users.length >>>',this.props.users.length  )
    return <div>
      <ul>
        ${this.props.users.map( user =>{
        return <li key={user.get('id')} className='user'>{user.getFullName()}</li>
      })}
      </ul>
      <button onClick={this.insertUser}></button>
    </div>
  }
})